:root {
  --white: #fff;
  --black: #000;
  --body: #000;
  --text: #2B317A;
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}
@media only screen and (max-width: 1007px) {
  html {
    font-size: 57%;
  }
}
@media only screen and (max-width: 830px) {
  html {
    font-size: 50%;
  }
}

body {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  line-height: 1.6;
  background-color: var(--white);
  min-height: 100vh;
}

.nav {
  width: 95%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: fixed;
  top: 0;
  left: 2.5%;
  height: 15vh;
}

.links-left,
.links-right {
  width: 40%;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  list-style: none;
  height: 60%;
}

.link {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
}

.link__a {
  text-decoration: none;
  font-size: 1.7rem;
  font-weight: 900;
  letter-spacing: 0.2rem;
  color: var(--black);
}

.logo {
  width: 20%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
}

.img-svg {
  width: 20rem;
  height: 20rem;
  position: absolute;
  top: -3rem;
}

.header {
  width: 95%;
  height: 100vh;
  margin: 0 auto;
}

.container {
  width: 100%;
  height: 100vh;
  display: grid;
  grid-template-columns: 1fr 1fr;
}

.inner__left {
  display: flex;
  justify-content: start;
}

.inner__right {
  grid-column: 2/3;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  overflow: hidden;
}

.svg-container {
  width: 30rem;
  height: 40rem;
  position: relative;
}

.abs-pos {
  position: absolute;
  top: 0;
}

.text__wrapp {
  justify-content: center;
  margin-left: 9rem;
  margin-top: 22rem;
}

.text__bg {
  position: fixed;
  top: 50%;
  left: 40%;
  transform: translate(-50%, -50%);
  font-size: 16rem;
  font-weight: bolder;
  color: var(--black);
  letter-spacing: 0.2rem;
  z-index: -1;
  opacity: 0.06;
  text-shadow: 9px 17px 25px rgba(0, 0, 0, 0.12);
}

.h2__text {
  font-size: 4.6rem;
  color: var(--text);
  font-weight: 900;
  letter-spacing: 0.4rem;
  line-height: 4.2rem;
}

.h5__text {
  font-size: 1.3rem;
  margin-top: 1rem;
  line-height: 1;
}

.text__p {
  margin-top: 1rem;
  font-weight: 500;
  font-size: 1.6rem;
  letter-spacing: 0.1rem;
}

.text__p__span {
  color: var(--text);
  font-size: 1.8rem;
  font-weight: 900;
  margin-right: 1rem;
}
