:root {
  --white: #fff;
  --black: #000;
  --body: #000;
  --text: #666;
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}
@media only screen and (max-width: 1007px) {
  html {
    font-size: 57%;
  }
}
@media only screen and (max-width: 830px) {
  html {
    font-size: 50%;
  }
}

body {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  line-height: 1.6;
  background-color: var(--body);
  min-height: 100vh;
  overflow: hidden;
}

.bg-video {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  z-index: -1;
  overflow: hidden;
}
.bg-video__content {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.nav {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 10vh;
  background-color: rgba(255, 255, 255, 0.8);
  z-index: 6;
  border-bottom: 1px solid #a67c00;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.logo {
  width: 15%;
  height: 60%;
  border: 4px solid #000;
  font-size: 1.3rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  letter-spacing: 0.2rem;
  margin-left: 3rem;
}

.nav-links {
  list-style: none;
  width: 60%;
  display: flex;
  justify-content: space-evenly;
}

.link {
  font-size: 1.7rem;
  font-weight: bold;
  letter-spacing: 0.1rem;
  padding: 0 5rem;
}

.overlay {
  width: 100%;
  height: 100vh;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 7;
  background-color: var(--black);
}

.header {
  position: relative;
}

.h1 {
  color: #a67c00;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 7;
  letter-spacing: 0.4rem;
  display: flex;
  align-items: center;
  justify-content: center;
}

.letter {
  font-size: 10rem;
  padding: 0 2rem;
}

.first {
  font-family: "Charm", cursive;
  font-weight: 700;
  font-size: 14rem;
}

.h3 {
  color: #A99319;
  font-size: 4rem;
  z-index: 4;
  position: absolute;
  top: 70%;
  left: 50%;
  transform: translateX(-50%);
  letter-spacing: 0.2rem;
  z-index: 7;
}

.overlay2 {
  width: 100%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.85);
  position: absolute;
  z-index: 2;
}
